import React, {
    useState, useEffect, useRef} from "react";
import { useNavigate, useParams } from "react-router-dom";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://localhost:3001"); 

const Room = () => {
    const [conferences, setConferences] = useState({});
    const [stream, setStream] = useState(null);
    const { roomID } = useParams();
    const navigate = useNavigate()
    const [showModal, setShowModal] = useState(false);
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const [pc, setPC] = useState(null);
    const [typeUser, setTypeUser] = localStorage.getItem("typeUser");
    const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const canvasRef = useRef(null);
  const isBroadcaster = typeUser === true;


    //Interactive desk

    useEffect(() => {
      socket.emit("joinRoom", roomID);

      socket.on("draw", (data) => {
        const ctx = canvasRef.current.getContext("2d");
        ctx.beginPath();
        ctx.moveTo(data.prevX, data.prevY);
        ctx.lineTo(data.x, data.y);
        ctx.strokeStyle = data.color;
        ctx.lineWidth = data.lineWidth;
        ctx.stroke();
      });

      return () => {
        socket.off("draw");
      };
    }, [roomID]);

    const handleMouseDown = (e) => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      const rect = canvas.getBoundingClientRect();

      const prevX = e.clientX - rect.left;
      const prevY = e.clientY - rect.top;

      const draw = (e) => {
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;

        ctx.beginPath();
        ctx.moveTo(prevX, prevY);
        ctx.lineTo(x, y);
        ctx.strokeStyle = "black"; // Цвет линии
        ctx.lineWidth = 2; // Ширина линии
        ctx.stroke();

        // Отправляем данные на сервер
        socket.emit("draw", {
          prevX,
          prevY,
          x,
          y,
          color: "black",
          lineWidth: 2,
        });
      };

      canvas.addEventListener("mousemove", draw);

      canvas.addEventListener("mouseup", () => {
        canvas.removeEventListener("mousemove", draw);
      });
    };




    //Chat part

    useEffect(() => {
      socket.emit("joinRoom", { roomID });

      socket.on("message", (message) => {
        setMessages((prevMessages) => [...prevMessages, message]);
      });

      return () => {
        socket.off();
      };
    }, [roomID]);

    const sendMessage = (e) => {
      e.preventDefault();
      if (message) {
        socket.emit("sendMessage", { roomID, message });
        setMessage("");
      }
    };


    const GoToBack = () => {
        navigate("/")
    }

    const handleModalChat = () => {
      setShowModal(true);
    };

    const handleModalClose = () => {
      setShowModal(false);
    };

    const handleLeaveConference = (roomID) => {
      if (typeUser) {
        // Отправляем запрос на сервер для удаления конференции
        fetch(`http://localhost:3001/delete-conference/${roomID}`, {
          method: "DELETE",
        })
          .then((response) => response.json())
          .then((data) => {
            // Удаляем конференцию из списка
            setConferences((prevConferences) => {
              const newConferences = { ...prevConferences };
              delete newConferences[roomID];
              return newConferences;
            });
              navigate("/")
          })
          .catch((error) => console.error(error));
      } else {
        navigate("/")
      }
    };


  return (
    <div className="Room">
      <div className="header-box">
        <div className="header-text">Конференция</div>
      </div>
      <div>
        {!showModal && (
          <button onClick={() => handleLeaveConference(roomID)}>Выйти</button>
        )}
      </div>
      <div>
        <div>
          <h2>Room ID: {roomID}</h2>
          <canvas
            ref={canvasRef}
            width={800}
            height={600}
            style={{ border: "1px solid black" }}
            onMouseDown={isBroadcaster ? handleMouseDown : null}
          />
          {!isBroadcaster && <p>Вы наблюдатель. Рисование недоступно.</p>}
        </div>
      </div>
      {!showModal && (
        <div className="chat-box" onClick={handleModalChat}>
          <div className="chat-icon"></div>
        </div>
      )}
      {showModal && (
        <div>
          <div>
            <h2 className="title-modal">Chat Room</h2>
            <div>
              {messages.map((msg, index) => (
                <div key={index}>
                  <div className="message-container"></div>
                  <strong>{msg.user}: </strong>
                  {msg.text}
                </div>
              ))}
            </div>
            <form onSubmit={sendMessage}>
              <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type a message..."
              />
              <button type="submit">Send</button>
            </form>
            <div className="modal-close" onClick={handleModalClose}>
              ×
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Room;
