import React, {
    useState, useEffect, useRef} from "react";
import { useNavigate, useParams } from "react-router-dom";
import io from "socket.io-client";
import "./App.css";

const socket = io("http://localhost:3001"); 

const Room = () => {
    const [stream, setStream] = useState(null);
    const { roomID } = useParams();
    const navigate = useNavigate()
    const [showModal, setShowModal] = useState(false);
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const [pc, setPC] = useState(null);
    const [typeUser, setTypeUser] = localStorage.getItem("typeUser");
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");

    //ViderTransfer part

    useEffect(() => {
      // Получение доступа к камере и микрофону
      navigator.mediaDevices
        .getUserMedia({ video: true, audio: true })
        .then((stream) => {
          setLocalStream(stream);
        })
        .catch((error) => {
          console.error("Error getting user media:", error);
        });
    }, []);

    useEffect(() => {
      if (localStream) {
        // Создание RTCPeerConnection
        const pc = new RTCPeerConnection();
        setPC(pc);

        // Добавление медиа-стримов
        localStream.getTracks().forEach((track) => {
          pc.addTrack(track, localStream);
        });

        // Создание SDP-офертов
        pc.createOffer()
          .then((offer) => {
            return pc.setLocalDescription(
              new RTCSessionDescription({ type: "offer", sdp: offer })
            );
          })
          .then(() => {
            // Обмен SDP-офертами
            socket.emit("offer", { roomID, sdp: pc.localDescription.sdp });
          })
          .catch((error) => {
            console.error("Error creating offer:", error);
          });
      }
    }, [localStream]);

    useEffect(() => {
      // Установление соединения
      socket.on("answer", ({ sdp }) => {
        pc.setRemoteDescription(
          new RTCSessionDescription({ type: "answer", sdp })
        );
      });

      socket.on("ice", ({ candidate }) => {
        pc.addIceCandidate(new RTCIceCandidate(candidate));
      });
    }, [pc]);

    const handleVideo = () => {
      // Отображение видео
      const video = document.getElementById("video");
      video.srcObject = remoteStream;
    };




    //Chat part

    useEffect(() => {
      socket.emit("joinRoom", { roomID });

      socket.on("message", (message) => {
        setMessages((prevMessages) => [...prevMessages, message]);
      });

      return () => {
        socket.off();
      };
    }, [roomID]);

    const sendMessage = (e) => {
      e.preventDefault();
      if (message) {
        socket.emit("sendMessage", { roomID, message });
        setMessage("");
      }
    };


    const GoToBack = () => {
        navigate("/")
    }

    const handleModalChat = () => {
      setShowModal(true);
    };

    const handleModalClose = () => {
      setShowModal(false);
    };




  return (
    <div className="Room">
      <div className="header-box">
        <div className="header-text">Конференция</div>
      </div>
      <div>{!showModal && <button onClick={GoToBack}>Выйти</button>}</div>
      {!showModal && (
        <div className="chat-box" onClick={handleModalChat}>
          <div className="chat-icon"></div>
        </div>
      )}
      <video id="video" width="640" height="480" autoPlay></video>
      <button onClick={handleVideo}>Play Video</button>
      {showModal && (
        <div>
          <div>
            <h2 className="title-modal">Chat Room</h2>
            <div>
              {messages.map((msg, index) => (
                <div key={index}>
                  <div className="message-container"></div>
                  <strong>{msg.user}: </strong>
                  {msg.text}
                </div>
              ))}
            </div>
            <form onSubmit={sendMessage}>
              <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type a message..."
              />
              <button type="submit">Send</button>
            </form>
            <div className="modal-close" onClick={handleModalClose}>
              ×
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Room;
