// src/Admin.js
import React, { useEffect, useRef } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:3001"); // Замените на ваш URL сервера

const Admin = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    const handleStream = (data) => {
      const videoElement = document.createElement("video");
      videoElement.srcObject = data.stream;
      videoElement.play();
      document.body.appendChild(videoElement);
    };

    socket.on("stream", handleStream);

    return () => {
      socket.off("stream", handleStream);
    };
  }, []);

  const startStreaming = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({
      video: true,
      audio: true,
    });
    videoRef.current.srcObject = stream;

    // Отправка потока на сервер
    socket.emit("adminStream", { stream });
  };

  return (
    <div>
      <h1>Admin Streaming</h1>
      <button onClick={startStreaming}>Start Streaming</button>
      <video ref={videoRef} autoPlay muted style={{ width: "400px" }} />
    </div>
  );
};

export default Admin;
