// src/Admin.js
import React, { useEffect, useRef } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:3001"); // Замените на ваш URL сервера

const RoomAdmin = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    const startStreaming = async () => {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      videoRef.current.srcObject = stream;

      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorder.start(1000); // Сохранять данные каждые 1000 мс

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          socket.emit("stream", event.data);
        }
      };
    };

    startStreaming();
  }, []);

  return (
    <div>
      <h1>Admin Streaming</h1>
      <video ref={videoRef} autoPlay muted style={{ width: "400px" }} />
    </div>
  );
};

export default RoomAdmin;
