// src/Member.js
import React, { useEffect, useRef } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:3001"); // Замените на ваш URL сервера

const Member = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    const handleStream = (data) => {
      if (videoRef.current) {
        videoRef.current.srcObject = data.stream;
        videoRef.current.play();
      }
    };

    socket.on("stream", handleStream);

    return () => {
      socket.off("stream", handleStream);
    };
  }, []);

  return (
    <div>
      <h1>Member View</h1>
      <video ref={videoRef} autoPlay style={{ width: "400px", height }} />
    </div>
  );
};

export default Member;
