import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./App.css";

const Conference = () => {
  const [showModal, setShowModal] = useState(false);
  const [conferenceName, setConferenceName] = useState("");
  const [conferences, setConferences] = useState([]);

  const handleCreateConference = () => {
    setShowModal(true);
  };

  const handleModalSubmit = (e) => {
    e.preventDefault();
    const newConference = {
      name: conferenceName,
      admin: "Admin Name", // replace with actual admin name
    };
    setConferences([...conferences, newConference]);
    setShowModal(false);
    setConferenceName("");
  };

  return (
    <div className="Conference">
      <div className="header-box">
        <div className="header-text">Конференции</div>
      </div>
      <div className="conference-catalog">
        {conferences.map((conference, index) => (
          <div key={index} className="conference-box">
            <img className="user-image" src={""} alt="userImage" />
            <div className="conference-name">{conference.name}</div>
            <div className="conference-admin">Проводит: {conference.admin}</div>
            <div className="join-btn">
              <div className="join-text" onClick={""}>
                Войти
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="create-conference-btn-helper">
        <div className="create-conference-btn" onClick={handleCreateConference}>
          <div className="create-conference-btn-text">Создать</div>
        </div>
      </div>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Создать конференцию</h2>
            <form onSubmit={handleModalSubmit}>
              <input
                type="text"
                value={conferenceName}
                onChange={(e) => setConferenceName(e.target.value)}
                placeholder="Введите название конференции"
              />
              <button type="submit">Начать</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Conference;
